/services/vault/config/policies/master/a.rules:
path "app-sre/creds/*" {
  capabilities = ["read"]
}
path "app-sre/ansible/*" {
  capabilities = ["read"]
}
#app-interface secrets integration
path "app-interface/*" {
   capabilities = ["read"]
}
# integrations input/output
path "app-sre/integrations-input/*" {
  capabilities = ["read"]
}
path "app-sre/integrations-output/*" {
  capabilities = ["create", "update", "read"]
}
path "app-sre/integrations-throughput/*" {
  capabilities = ["create", "update", "read"]
}

path "devtools-osio-ci/*" {
  capabilities = ["create", "read", "update", "delete", "list"]
}
path "app-sre/*" {
  capabilities = ["create", "read", "update", "delete", "list"]
}
path "app-interface/*" {
  capabilities = ["create", "read", "update", "delete", "list"]
}
path "totp/app-sre/keys/*" {
  capabilities = ["create", "read", "update", "delete", "list"]
}
path "totp/app-sre/code/*" {
  capabilities = ["read", "list"]
}

# https://www.vaultproject.io/api/system/audit-hash#calculate-hash
path "/sys/audit-hash/file" {
  capabilities = ["create", "read", "update"]
}

#allow vault seal/unseal
path "/sys/seal" {
  policy = "sudo"
}
path "/sys/unseal" {
  policy = "sudo"
}

path "devtools-osio-ci/*" {
  capabilities = ["create", "read", "update", "delete", "list"]
}
